-- Table structure for jc_content
-- ----------------------------
DROP TABLE IF EXISTS `jc_content`;
CREATE TABLE `jc_content`  (
  `id` int(11) NOT NULL COMMENT '全局唯一标识',
  `channel_id` int(11) NOT NULL COMMENT '栏目ID',
  `user_id` int(11) NOT NULL COMMENT '撰写管理员ID',
  `publish_user_id` int(11) NULL DEFAULT NULL COMMENT '发布管理员',
  `model_id` int(11) NOT NULL COMMENT '模型ID',
  `site_id` int(11) NOT NULL COMMENT '站点ID',
  `title` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容标题',
  `title_is_bold` tinyint(1) NOT NULL COMMENT '内容标题是否加粗',
  `title_color` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '内容标题的颜色',
  `short_title` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '简短标题',
  `release_time` datetime(0) NOT NULL COMMENT '发布时间',
  `offline_time` datetime(0) NULL DEFAULT NULL COMMENT '下线时间',
  `content_secret_id` int(11) NULL DEFAULT NULL COMMENT '内容密级',
  `status` smallint(6) NOT NULL DEFAULT 0 COMMENT '内容状态(1:草稿;  2-初稿   3:流转中;   4:已审核;   5:已发布;  6:退回;  7:下线  8-归档 9 暂存 10 驳回 )',
  `create_type` smallint(6) NULL DEFAULT NULL COMMENT '创建方式（1:直接创建    2:投稿  3:站群推送   4:站群采集   5:复制  6:链接型引用 7:镜像型引用）8 外部采集',
  `is_edit` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否编辑（0-否   1-是）',
  `sort_num` int(11) NOT NULL DEFAULT 0 COMMENT '排序值',
  `sort_weight` int(11) NOT NULL DEFAULT 0 COMMENT '排序值权重(排序值相同情况下，权重越大，排序越前)',
  `views` int(11) NOT NULL DEFAULT 0 COMMENT '浏览量',
  `comments` int(11) NOT NULL DEFAULT 0 COMMENT '评论量',
  `ups` int(11) NOT NULL DEFAULT 0 COMMENT '点赞数',
  `downs` int(11) NOT NULL DEFAULT 0 COMMENT '点踩数',
  `downloads` int(11) NOT NULL DEFAULT 0 COMMENT '下载量',
  `view_control` smallint(6) NOT NULL DEFAULT 1 COMMENT '浏览设置（1-允许游客访问   2-登录后访问）',
  `comment_control` smallint(6) NOT NULL DEFAULT 2 COMMENT '评论设置(1允许游客评论 2登录后评论  3不允许评论)',
  `is_top` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否置顶',
  `top_start_time` datetime(0) NULL DEFAULT NULL COMMENT '置顶开始时间',
  `top_end_time` datetime(0) NULL DEFAULT NULL COMMENT '置顶结束时间',
  `is_release_pc` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否发布至pc（0-否  1-是）',
  `is_release_wap` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否发布至wap（0-否  1-是）',
  `is_release_app` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否发布至app（0-否  1-是）',
  `is_release_miniprogram` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否发布至小程序（0-否  1-是）',
  `is_recycle` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否加入回收站（0-否  1-是）',
  `copy_source_content_id` int(11) NULL DEFAULT NULL COMMENT '复制来源内容id',
  `has_static` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否已生成静态化页面',
  `create_time` datetime(0) NOT NULL COMMENT '创建时间',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '修改时间',
  `create_user` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '创建者',
  `update_user` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '修改者',
  `deleted_flag` tinyint(1) NOT NULL COMMENT '删除标识（0为正常1为删除）',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `index_jc_content_channel`(`channel_id`) USING BTREE,
  INDEX `index_jc_content_user`(`user_id`) USING BTREE,
  INDEX `index_jc_content_model`(`model_id`) USING BTREE,
  INDEX `index_jc_content_site`(`site_id`) USING BTREE,
  INDEX `index_jc_content_status`(`status`) USING BTREE,
  INDEX `index_jc_content_release`(`release_time`) USING BTREE,
  INDEX `index_jc_content_offonline`(`offline_time`) USING BTREE,
  INDEX `index_jc_content_publish_user`(`publish_user_id`) USING BTREE,
  INDEX `index_jc_content_create_type`(`create_type`) USING BTREE,
  INDEX `Index_jc_content_is_edit`(`is_edit`) USING BTREE,
  INDEX `Index_jc_content_sort_num`(`sort_num`) USING BTREE,
  INDEX `Index_jc_content_sort_num_weight`(`sort_weight`) USING BTREE,
  INDEX `Index_jc_content_views`(`views`) USING BTREE,
  INDEX `Index_jc_content_comments`(`comments`) USING BTREE,
  INDEX `Index_jc_content_ups`(`ups`) USING BTREE,
  INDEX `Index_jc_content_downs`(`downs`) USING BTREE,
  INDEX `Index_jc_content_downloads`(`downloads`) USING BTREE,
  INDEX `Index_jc_content_is_allow_comments`(`comment_control`) USING BTREE,
  INDEX `Index_jc_content_is_top`(`is_top`) USING BTREE,
  INDEX `Index_jc_content_is_cycle`(`is_recycle`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '内容主体表' ROW_FORMAT = Compact;

