INSERT INTO `jc_content_txt` VALUES (708, 2626, 'content', '<p><span style=\"color: #191919; font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; background-color: #FFFFFF;\">当日，第七届奥塔基风筝节在奥塔基海滩举行。来自世界各地的上百个大型风筝一齐升空，成为一道海岸风景。</span></p>');
INSERT INTO `jc_content_txt` VALUES (709, 2575, 'content', '<p><br/></p><p><br/></p><p style=\"border: 0px; margin: 0.63em 0px 1.8em; padding: 0px; font-size: 16px; counter-reset: list-1 0 list-2 0 list-3 0 list-4 0 list-5 0 list-6 0 list-7 0 list-8 0 list-9 0; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"border: 0px; margin: 0px; padding: 0px; font-size: 16px;\">“一年中，没有哪个季节像秋天一样，叫人沉醉。”</span></p><p style=\"border: 0px; margin: 0.63em 0px 1.8em; padding: 0px; font-size: 16px; counter-reset: list-1 0 list-2 0 list-3 0 list-4 0 list-5 0 list-6 0 list-7 0 list-8 0 list-9 0; color: rgb(25, 25, 25); font-family: &quot;PingFang SC&quot;, Arial, 微软雅黑, 宋体, simsun, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"border: 0px; margin: 0px; padding: 0px; font-size: 16px;\"><strong style=\"font-weight: 700; border: 0px; margin: 0px; padding: 0px; font-size: 16px;\"><span style=\"border: 0px; margin: 0px; padding: 0px; font-size: 16px;\">你是否幻想过一场关于秋天的旅行？</span></strong></span></p><p style=\"border: 0px; margin: 0.63em 0px 1.8em; padding: 0px; font-size: 16px; counter-reset: list-1 0 list-2 0 list-3 0 list-4 0 list-5 0 list-6 0 list-7 0 list-8 0 list-9 0; color: rgb(25, 25, 25); font-family: &q