INSERT INTO `jc_content_txt` VALUES (793, 2907, 'content', '<p><span style=\"font-size: 14px; color: #3F3F3F;\">腾讯推出了一款全新的聊天软件：TIM，不同的是它仅针对办公场景，让职场沟通更为方便高效。这款应用的界面非常简洁，与QQ很相似，只不过在功能方面多了协作文档、邮件、日程、电话等等服务，对于职场人士来说更为常用。它支持使用QQ号登录，并且好友以及消息是完全同步的，不会错过重要信息和内容。</span></p><h2 class=\"section-title\" style=\"margin: 0px; padding: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; float: none; border: 0px none;\"><span style=\"font-size: 16px; color: #3F3F3F;\">腾讯TIM电脑版软件截图</span></h2><ul class=\"screenshot-items clearfix list-paddingleft-2\" style=\"list-style-type: none;\"><li><p><a href=\"http://xiazai.zol.com.cn/picture_index_71/index705206.shtml\" class=\"pic\" style=\"color: rgb(51, 51, 51); text-decoration-line: none; outline: none 0px; word-break: break-all;\"><img alt=\"腾讯TIM电脑版 2.3.2\" title=\"[腾讯TIM电脑版] 点击看大图\" src=\"https://b.zol-img.com.cn/soft/7_400x250/206/cekgdneIt5dnw.jpg\" style=\"border: 0px; vertical-align: middle; opacity: 1;\"/></a>&nbsp; &nbsp;<a href=\"http://xiazai.zol.com.cn/picture_index_71/index705205.shtml\" class=\"pic\" style=\"color: rgb(51, 51, 51); text-decoration-line: none; outline: none 0px; word-break: break-all;\"><img alt=\"腾讯TIM电脑版 2.3.2\" title=\"[腾讯TIM电脑版] 点击看大图\" src=\"https://b.zol-img.com.cn/soft/7_400x250/205/ceK9NSBuiiQW2.png\" style=\"border: 0px; vertical-align: middle; opacity: 1;\"/></a></p></li></ul><p><br/></p><h2 class=\"section-title\" style=\"margin: 0px; padding: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; float: none; border: 0px none;\"><span style=\"font-size: 16px; color: #3F3F3F;\">腾讯TIM电脑版软件简介</span></h2><p><span style=\"font-size: 14px; color: #3F3F3F;\">腾讯推出了一款全新的聊天软件：TIM，不同的是它仅针对办公场景，让职场沟通更为方便高效。这款应用的界面非常简洁，与QQ很相似，只不过在功能方面多了协作文档、邮件、日程、电话等等服务，对于职场人士来说更为常用。它支持使用QQ号登录，并且好友以及消息是完全同步的，不会错过重要信息和内容。<br/>腾讯tim电脑版特色介绍：<br/>一、熟悉的QQ习惯<br/>简洁的操作界面，轻量易用;QQ号登录，好友、消息完全同步